import React, { useState } from 'react';

interface CalculatorFormProps {
  onCalculate: (inputData: any) => void;
}

const CalculatorInputForm: React.FC<CalculatorFormProps> = ({ onCalculate }) => {
  const [frequency, setFrequency] = useState('');
  const [areaSize, setAreaSize] = useState('');
  const [stateOfLot, setStateOfLot] = useState('');
  const [congestionFactor, setCongestionFactor] = useState('');
  const [discount, setDiscount] = useState('')

  const handleInputChange = (event: React.ChangeEvent<HTMLSelectElement | HTMLInputElement>) => {
    const { name, value } = event.target;
    setFormData({ ...formData, [name]: value });
  };
  const handleSubmit = (e: React.FormEvent) => {
    e.preventDefault();
    onCalculate({ frequency, areaSize, stateOfLot, congestionFactor, discount });
  };

  const frequencyOptions = [
    { value: "1", label: "One-time" },
    { value: "1x/week", label: "Weekly" },
    { value: "2x/month", label: "Every two weeks" },
    { value: "2.17x/month", label: "Every two weeks (2.17x/month)" },
    { value: "4x/month", label: "Every other week" },
    // Add more frequencies as needed
  ];

  const stateOfLotOptions = [
    { value: "1", label: "Low issues" },
    { value: "2", label: "Medium issues" },
    { value: "3", label: "High issues" },
  ];

  const congestionFactorOptions = [
    { value: "1", label: "Low congestion" },
    { value: "2", label: "Medium congestion" },
    { value: "3", label: "High congestion" },
  ];

  return (
    <form onSubmit={handleSubmit}>
      <label>
        Frequency:
        <input type="text" value={frequency} onChange={(e) => setFrequency(e.target.value)} />
      </label>
      <label>
        Area Size (Acres):
        <input type="number" value={areaSize} onChange={(e) => setAreaSize(e.target.value)} />
      </label>
      <label>
        State of Lot:
        <input type="text" value={stateOfLot} onChange={(e) => setStateOfLot(e.target.value)} />
      </label>
      <label>
        Congestion Factor:
        <input type="text" value={congestionFactor} onChange={(e) => setCongestionFactor(e.target.value)} />
      </label>
      <label>
        Discount %:
        <input type="number" value={discount} onChange={(e) => setDiscount(e.target.value)} />
      </label>
      <button type="submit">Calculate</button>
    </form>
  );
};

export default CalculatorInputForm;
