// BidCalculator.tsx
import React, { useState } from "react";

// Define the frequency key locally within this component
const frequencyKey = [
  { frequency_name: "Monthly", frequency_multiplier: 1, minutes_per_acre: 20 },
  { frequency_name: "Every other week", frequency_multiplier: 2, minutes_per_acre: 17 },
  { frequency_name: "Every two weeks", frequency_multiplier: 2.17, minutes_per_acre: 15 },
  { frequency_name: "1x/week", frequency_multiplier: 4.33, minutes_per_acre: 13 },
  { frequency_name: "2x/week", frequency_multiplier: 8.67, minutes_per_acre: 10 },
  { frequency_name: "3x/week", frequency_multiplier: 13, minutes_per_acre: 10 },
  { frequency_name: "4x/week", frequency_multiplier: 17.33, minutes_per_acre: 10 },
  { frequency_name: "5x/week", frequency_multiplier: 21.67, minutes_per_acre: 10 },
  { frequency_name: "Daily", frequency_multiplier: 30.4, minutes_per_acre: 10 }
];

interface InputValues {
  frequencyName: string;
  sizeOfArea: number;
  discount?: number;
}

interface BidCalculatorProps {
  onCalculate: (result: { jobTime: number; estimatedCost: number; totalBidAmount: number }) => void;
}

const BidCalculator: React.FC<BidCalculatorProps> = ({ onCalculate }) => {
  const [inputValues, setInputValues] = useState<InputValues>({
    frequencyName: "Monthly",
    sizeOfArea: 1,
    discount: 0,
  });

  const handleInputChange = (e: React.ChangeEvent<HTMLInputElement | HTMLSelectElement>) => {
    const { name, value } = e.target;
    setInputValues({
      ...inputValues,
      [name]: name === "sizeOfArea" || name === "hourlyRate" || name === "discount" ? Number(value) : value,
    });
  };

  const handleCalculate = () => {
    const selectedFrequency = frequencyKey.find(
      (freq) => freq.frequency_name === inputValues.frequencyName
    );

    if (!selectedFrequency) {
      console.error("Invalid frequency selected");
      return;
    }

    const { frequency_multiplier, minutes_per_acre } = selectedFrequency;

    const jobTime = minutes_per_acre * inputValues.sizeOfArea * frequency_multiplier;
    const estimatedCost = (inputValues.hourlyRate / 60) * jobTime;
    const discount = inputValues.discount ? 1 - inputValues.discount / 100 : 1;
    const totalBidAmount = estimatedCost * discount;

    onCalculate({ jobTime, estimatedCost, totalBidAmount });
  };

  return (
    <div>
      <h2>Bid Calculator</h2>
      <div>
        <label>
          Frequency of Service:
          <select name="frequencyName" value={inputValues.frequencyName} onChange={handleInputChange}>
            {frequencyKey.map((freq) => (
              <option key={freq.frequency_name} value={freq.frequency_name}>
                {freq.frequency_name}
              </option>
            ))}
          </select>
        </label>
      </div>
      <div>
        <label>
          Size of Area (Acres):
          <input type="number" name="sizeOfArea" value={inputValues.sizeOfArea} onChange={handleInputChange} />
        </label>
      </div>
      <div>
        <label>
          Hourly Rate ($):
          <input type="number" name="hourlyRate" value={inputValues.hourlyRate} onChange={handleInputChange} />
        </label>
      </div>
      <div>
        <label>
          Discount (%):
          <input type="number" name="discount" value={inputValues.discount} onChange={handleInputChange} />
        </label>
      </div>
      <button onClick={handleCalculate}>Calculate</button>
    </div>
  );
};

export default BidCalculator;
