import React, { useState } from 'react';

interface CalculationInputs {
  frequency: string;
  areaSize: number;
  stateOfLot: number;
  congestionFactor: number;
  discount: number;
}

interface CalculationResults {
  hourlyRate: number;
  jobTimeEstimate: number;
  estimatedCost: number;
  totalBidAmount: number;
  totalMonthlyAmount: number;
}

const CalculatorForm = () => {
  const [frequency, setFrequency] = useState('');
  const [areaSize, setAreaSize] = useState<number | ''>('');
  const [stateOfLot, setStateOfLot] = useState<number>(1);
  const [congestion, setCongestion] = useState<number>(1);
  const [discount, setDiscount] = useState<number>(0);

  const handleCalculate = (inputs: CalculationInputs): CalculationResults => {
    const { frequency, areaSize, stateOfLot, congestionFactor, discount } = inputs;

    // Lookup frequency data from the frequency key
    const frequencyData = frequencyKey.find((f) => f.key === frequency);
    if (!frequencyData) throw new Error("Invalid frequency");

    const minutesPerAcre = frequencyData.minutesPerAcre;
    const avgDaysPerMonth = frequencyData.avgDaysPerMonth;

    // Calculate the hourly rate based on area and adjustments
    const baseRate = 250; // Example base rate
    const hourlyRate =
      baseRate -
      (areaSize > 1 ? (areaSize - 1) * 5 : 0) +
      (stateOfLot - 1) * 15 +
      (congestionFactor - 1) * 15;

    // Ensure hourly rate is within a minimum threshold
    const adjustedHourlyRate = Math.max(210, hourlyRate);

    // Calculate estimated job time per service based on area and frequency
    const jobTimeEstimate = Math.max(
      10,
      minutesPerAcre * areaSize * (1 + (congestionFactor === 2 ? 0.15 : congestionFactor === 3 ? 0.25 : 0))
    );

    // Calculate estimated cost per service (time in hours * hourly rate)
    const estimatedCost = (jobTimeEstimate / 60) * adjustedHourlyRate;

    // Apply discount to calculate total bid amount per service
    const totalBidAmount = estimatedCost * (1 - discount / 100);

    // Calculate monthly total bid by multiplying by average number of days per month
    const totalMonthlyAmount = totalBidAmount * avgDaysPerMonth;

    return {
      hourlyRate: adjustedHourlyRate,
      jobTimeEstimate,
      estimatedCost,
      totalBidAmount,
      totalMonthlyAmount,
    };
  };

  const [results, setResults] = useState<CalculationResults | null>(null);

  const onCalculate = () => {
    const inputs: CalculationInputs = {
      frequency,
      areaSize,
      stateOfLot,
      congestionFactor,
      discount,
    };
    const calculationResults = handleCalculate(inputs);
    setResults(calculationResults);
  };

  return (
    <div>
      <h1>Bid Calculator</h1>
      {/* Form fields for input values */}
      <button onClick={onCalculate}>Calculate</button>

      {results && (
        <div>
          <h2>Results</h2>
          <p>Hourly Rate: ${results.hourlyRate.toFixed(2)}</p>
          <p>Estimated Job Time (minutes): {results.jobTimeEstimate}</p>
          <p>Estimated Cost: ${results.estimatedCost.toFixed(2)}</p>
          <p>Total Bid Amount per Service: ${results.totalBidAmount.toFixed(2)}</p>
          <p>Total Bid Amount per Month: ${results.totalMonthlyAmount.toFixed(2)}</p>
        </div>
      )}
    </div>
  );
};

export default CalculatorForm;
